		case UNITAI_ESCORT_SEA:
			iValue += iCombatValue;
			iValue += (iCombatValue * GC.getUnitInfo(eUnit).getMoves());
			iValue += (GC.getUnitInfo(eUnit).getInterceptionProbability() * 3);
